from django.test import override_settings
from django.urls import reverse

from rest_framework import status

from test_helpers.clients import DataTestClient


class CertificateDownload(DataTestClient):
    @override_settings(
        P12_CERTIFICATE="",  # /PS-IGNORE
        CERTIFICATE_PASSWORD="testing",
        SIGNING_EMAIL="test@example.com",  # /PS-IGNORE
    )
    def test_certificate_download(self):
        url = reverse("documents:certificate")
        response = self.client.get(url, **self.exporter_headers)

        self.assertEqual(response.status_code, status.HTTP_200_OK)
        self.assertEqual(
            response["content-type"],
            "application/x-x509-ca-cert",
        )
        self.assertEqual(
            response["content-disposition"],
            'attachment; filename="LITECertificate.crt"',
        )
